import { useState,  useEffect} from 'react'
import './App.css'
import axios from 'axios'

const App = () => {
  useEffect(() => {
    const intervalId = setInterval(() => {  //assign interval to a variable to clear it.
      axios
      .get('http://localhost:3001/notes')
      .then(response => {
        console.log('render new data')
        setNotes(response.data.filter(note=>{(Date.now() - Date.parse(note.snapshotTimestamp)) < 600000}))
      })
    }, 2000)

    return () => clearInterval(intervalId);

  }, [])

  const [notes, setNotes] = useState([
    { serialNumber:'', snapshotTimestamp:'', closestDistance: 0, owner: '', ownerEmail: '' , phone:''}
  ])


  const showNotes = notes.map(note=> {
                                          return(<tr>
                                            <td key = "owner">{note.owner}</td >
                                            <td key = "phone">{note.phone}</td>
                                            <td key = "email">{note.ownerEmail}</td>
                                            <td key = "serialNumber">{note.serialNumber}</td>
                                            <td key ="snapshotTimestamp"> {note.snapshotTimestamp}</td>
                                            <td key = "closestDistance"> {note.closestDistance}</td></tr>)})



  return (
    <div>
      <h2>Info</h2>
      <>
      <table cellPadding="10"><caption>The pilots who recently violated the NDZ perimeter</caption><tbody><tr><td>Owner</td><td>Phone</td><td>Email</td><td>serialNumber</td><td>Last time</td><td>closestDistance</td></tr>{showNotes}</tbody></table>
      </>
    </div>

  )
}
export default App
